mapper.select:
	   	select hno, htitle, hregday, hwriter, hcontent, himg from(
	   		select row_number() over(order by hno desc) rn, hno, htitle, hregday, hwriter, hcontent, himg
	   		from hugi h inner join jumun j on h.jno=j.jno where rownum <= #{nowPage} * #{amount} and j.pno=#{pno})
   		where rn > (#{nowPage} -1) * #{amount}

mapper.update:
update hugi
htitle=#{htitle}, hcontent=#{hcontent}, himg=#{himg},
where hno=#{hno}
