mapper.select:
select catecode, pno, (select c.corp_name from product p inner join corp c on p.corp_id = c.corp_id where pno=#{pno} and rownum=1) as cname, pthumbnail, pname, pcontent, price from product p where pno=#{pno} and rownum=1
select product_seq.nextval from dual
insert into product values (#{pno}, #{pcategory}, #{price}, #{pname}, #{pstock}, #{pcontent}, #{pthumbnail}, #{pordercnt}, #{pregday}, #{corpId})
select * from product where corp_id=#{corpId}
select pno, catecode, price, pname, pstock, pcontent, pthumbnail, pordercnt, pregday, corp_id as corpId from product where corp_id=#{corpId} and pno=#{pno}
update product
pname=#{pname}, price=#{price}, pstock=#{pstock}, pthumbnail=#{pthumbnail}, pcontent=#{pcontent},
where corp_id=#{corpId} and pno=#{pno}
delete from product where corp_id=#{corpId} and pno=#{pno}
