mapper.select:
		select catecode, pno, cname, pthumbnail, pname, price from(
			select catecode, pno, cname, pthumbnail, pname, price, row_number() over() rn
            from product_dto where rownum <= #{nowPage} * #{amount} and catecode=#{catecode})
       	where rn > (#{nowPage} -1) * #{amount}

mapper.update:
update product
pname=#{pname}, price=#{price}, pstock=#{pstock}, pthumbnail=#{pthumbnail}, pcontent=#{pcontent},
where corp_id=#{corpId} and pno=#{pno}
