mapper.select:
	   	select qqno, qqcategory, qqtitle, qqcontent, qqimg, qqregday, member_id from(
	   		select qqno, qqcategory, qqtitle, qqcontent, qqimg, qqregday, member_id, row_number() over(order by qqno desc) rn
	   		from qna_q where rownum <= #{nowPage} * #{amount} and pno=#{pno})
   		where rn > (#{nowPage} -1) * #{amount}

select qqno, qqcategory, qqtitle, qqcontent, qqimg, qqregday, member_id from(
	select qqno, qqcategory, qqtitle, qqcontent, qqimg, qqregday, member_id, row_number() over(order by qqno desc) rn
	from qna_q where rownum <= #{cri.nowPage} * #{cri.amount} and member_id=#{loginId})
where rn > (#{cri.nowPage} -1) * #{cri.amount}

mapper.update:
update qna_q
qqtitle=#{qqtitle}, qqcontent=#{qqcontent}, qqimg=#{qqimg},
where member_id=#{memberId} and qqno=#{qqno}

update qna_a

	qacontent=#{qacontent} 

where corp_id=#{corpId} and qano=#{qano}
